import React from 'react';
import PropTypes from 'prop-types';

class Item extends React.Component {
  constructor() {
    super();
    this.state = {
      btnRemoveDisabled: true,
    };
  }

  onSelectClick = () => {
    this.setState({
      btnRemoveDisabled: false,
    });
  }

  removeItem = ({ target }) => {
    target.removeItem();
  }

  render() {
    const { content } = this.props;
    const { btnRemoveDisabled } = this.state;

    return (
      <div className="Item">
        <button type="button" onClick={ this.onSelectClick }>Adicionar</button>
        { content }
        <button type="button" onClick={ this.removeItem } disabled={ btnRemoveDisabled }>Remover</button>
      </div>
    );
  }
}

export default Item;

Item.propTypes = {
  content: PropTypes.string.isRequired,
};
