import React from 'react';
import PropTypes from 'prop-types';

class Item extends React.Component {
  constructor() {
    super();
    this.state = {
      btnRemoveDisabled: true,
    };
  }

  onSelectClick = () => {
    this.setState({
      btnRemoveOn
    })
  }

  render() {
    const { content } = this.props;
    const { btnRemoveOn } = this.state;

    return (
      <div className="Item">
        <button type="button" onClick={this.onSelectClick}>Adicionar</button>
        { content }
        <button type="button" disabled={ btnRemoveOn }>Remover</button>
      </div>
    );
  }
}

export default Item;

Item.propTypes = {
  content: PropTypes.string.isRequired,
};
